$(document).ready(function(){"use strict";let externalLinks=function(){let internalHost=[location.hostname];let protocolPattern=/^https?:\/\//i;$("a").each(function(){let href=$(this).attr("href");if(protocolPattern.test(href)){let hrefHostname=$(new URL(href)).attr("hostname");if($.inArray(hrefHostname,internalHost)===-1){$(this).attr("target","_blank");$(this).attr("rel","noopener noreferer")}}})};$(document).ajaxSuccess(function(){externalLinks()});let init=function(){externalLinks();hljs.highlightAll()};init()});